import React, { useState } from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';
import "../style.css";

const Mainheader = styled.div`
    background-color: #13264e;
    width: 100%;
    height: 60px;
    color: white;
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 0px 50px;
    top:0;
    position: fixed;
`;

const HeaderLeft = styled.div`
    display: flex;
    align-items: center;
    cursor: pointer;
`;

const LoginPage = () => {
  const [isSignup, setIsSignup] = useState(false);
  const navigate = useNavigate();

  const handleTabClick = (tab) => {
    setIsSignup(tab === 'signup');
  };

  const handleTitleClick = () => {
    navigate('/');
  };
  const handleLoginSubmit = async (event) => {
    event.preventDefault();

    const id = event.target.elements[0].value;
    const password = event.target.elements[1].value;

    const response = await fetch('http://localhost:8080/api/users/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded',
      },
      body: `email=${encodeURIComponent(id)}&password=${encodeURIComponent(password)}`,
    });

    if (response.ok) {
      const { token } = await response.json();
      // 토큰을 로컬 스토리지에 저장
      localStorage.setItem('token', token);
      // 로그인 상태를 업데이트
      setIsLoggedIn(true);
    } else {
      // 에러 처리...
    }
  };

  const handleSignupSubmit = async (event) => {
    event.preventDefault();

    const id = event.target.elements[0].value;
    const password = event.target.elements[1].value;
    const major = event.target.elements[2].value;

    const response = await fetch(`http://localhost:8080/api/users/resister?email=${encodeURIComponent(id)}&password=${encodeURIComponent(password)}&department=${encodeURIComponent(major)}`, {
      method: 'POST',
    });

    if (response.ok) {
      // Handle successful signup...
    } else {
      // Handle error...
    }
  };

  return (
    <>
    <Mainheader>
        <HeaderLeft onClick={handleTitleClick}>
            <h2>SentiNews</h2>
        </HeaderLeft>
    </Mainheader>
    <div className="container">
      <h1>{isSignup ? '회원가입' : '로그인'}</h1>
      
      <ul className="links">
        <li
          className={!isSignup ? 'active' : ''}
          onClick={() => handleTabClick('signin')}
        >
          로그인
        </li>
        <li
          className={isSignup ? 'active' : ''}
          onClick={() => handleTabClick('signup')}
        >
          회원가입
        </li>
      </ul>

      <form onSubmit={isSignup ? handleSignupSubmit : handleLoginSubmit}>
        <div className="input__block">
          <input type="email" name="id" placeholder="이메일" />
        </div>
        <div className="input__block">
          <input type="password" name="password" placeholder="비밀번호" />
        </div>
        {isSignup && (
          <div className="input__block">
            <input type="text" name="major" placeholder="학과" />
          </div>
        )}
        <button className="signin__btn">
          {isSignup ? '회원가입' : '로그인'}
        </button>
      </form>
    </div>
    </>
  );
};

export default LoginPage;
